     ca_proppser_info b
WHERE a.PROPPSER_ID = b.PROPPSER_ID

  and a.APP_CODE = #{appCode}


  and b.PROPPSER_NAME = #{customerName}


  and a.status not in
  
    #{item}
  


  and a.PROPPSER_ID in
  
    #{item}
